var wind_speed = msg.payload;
var alert_flag = context.get("alert_flag");
if (typeof alert_flag == "undefined")
    alert_flag = false;
if (wind_speed > 20.00 && !alert_flag) {
    alert_flag = true;
    msg.alert = 1;
    context.set("alert_flag", alert_flag);
    return msg;
}
if (wind_speed <= 12.00 && alert_flag) {
    alert_flag = false;
    msg.alert = 0;
    context.set("alert_flag", alert_flag);
    return msg;
}

